        self.left = None 
        self.right = None
        self.val = val 

class BinaryTree:
    def __init__(self): 
        self.root = None

    def addNode(self, val):
        if self.root == None:
            self.root = Node(val)
        else: 
            self._add(val, self.root)

    def _add(self, val, node): 
        if val < node.val: 
            if node.left is not None: 
                self._add(val, node.left) 
            else: 
                node.left = Node(val) 
        else: 
            if node.right is not None: 
                self._add(val, node.right) 
            else:
                node.right = Node(val)

