def bubble_sort(lst: List[str]) -> List[str]:
    def swap(a: str, b: str) -> None:
        lst[lst.index(a)], lst[lst.index(b)] = lst[lst.index(b)], lst[lst.index(a)]

    def bubble_sort_helper(lst: List[str]) -> None:
        if len(lst) <= 1:
            return
        for i in range(len(lst)):
            if lst[i] > lst[i + 1]:
                swap(lst[i], lst[i + 1])
        bubble_sort_helper(lst[:-1])

