def knapsack_0_1(weights, values, capacity):
    n = len(values)
    matrix = [ [0]*(capacity+1) for _ in range(n+1) ]

    for i in range(1, n+1):
        for c in range(capacity+1):
            if weights[i-1] > c:
                matrix[i][c] = matrix[i-1][c]
            else:
                matrix[i][c] = max(matrix[i-1][c],
                                  values[i-1] + matrix[i-1][c-weights[i-1]])
    return matrix[i][c]

