    message = db.Column(db.String(100), nullable=False)

    def __init__(self, message):
        self.message = message

class MessageSchema(ma.Schema):
    class Meta:
        fields = ('id','message')

message_schema = MessageSchema()
messages_schema = MessageSchema(many=True)

@app.route('/messages', methods=['POST'])
def create_message():
    message = request.json['message']
    new_message = Message(message)
    db.session.add(new_message)
    db.session.commit()
    return message_schema.jsonify(new_message)

@app.route('/messages', methods=['GET'])
def get_messages():
    messages = Message.query.all()
    result = messages_schema.dump(messages)
    return jsonify(result)

@app.route('/messages/<int:id>', methods=['PUT'])
def update_message(id):
    message = request.json['message']
    message_to_update = Message.query.get(id)
    message_to_update.message = message
    db.session.commit()
    return message_schema.jsonify(message_to_update)

@app.route('/messages/<int:id>', methods=['DELETE'])
def delete_message(id):
    message_to_delete = Message.query.get(id)
    db.session.delete(message_to_delete)
