def quick_sort(array):
  if len(array) < 2:
    # base case, arrays with 0 or 1 element are already "sorted"
    return array
  else:
    pivot = array[0]
    # sub-array of all the elements less than the pivot
    less = [i for i in array[1:] if i <= pivot]
    # sub-array of all the elements greater than the pivot
    greater = [i for i in array[1:] if i > pivot]

    # Inserting new code to divide and conquer
    if (len(less) > 1 and len(greater) > 1):
      return quick_sort(less[:len(less) // 2]) + quick_sort(less[len(less) // 2:]) + [pivot] + quick_sort(greater[:len(greater) // 2]) + quick_sort(greater[len(greater) // 2:])
    else:
      return quick_sort(less) + [pivot] + quick_sort(greater)

