def longest_subsequence(s1, s2):
    n = len(s1) 
    m = len(s2) 

    LCS = [[0 for k in range(m+1)]for l in range(n+1)]  

    for i in range(n+1): 
        for j in range(m+1): 
            if i == 0 or j == 0: 
                LCS[i][j] = 0
            elif s1[i-1] == s2[j-1]: 
                LCS[i][j] = LCS[i-1][j-1]+1
            else: 
                LCS[i][j] = max(LCS[i-1][j], LCS[i][j-1]) 

    return LCS[n][m]

