def most_frequent_words(words, k):
    """Return the top k most frequent words in the given list."""
    word_counts = {}
    for word in words:
        if word in word_counts:
            word_counts[word] += 1
        else:
            word_counts[word] = 1
    sorted_word_counts = sorted(word_counts.items(), key=lambda x: x[1], reverse=True)
    return [word for word, count in sorted_word_counts[:k]]

