def get_checksum(account_number):
    n = 0
    for i, num in enumerate(account_number):
        n += int(num) * (i % 6 + 2)

    checksum = 11 - (n % 11)
    if checksum == 10:
        return 0
    elif checksum == 11:
        return 5
    else:
        return checksum

