def sort(data):
    """Generic sorting algorithm."""
    if len(data) == 0:
        return []
    else:
        pivot = data[0]
        less = [i for i in data[1:] if i <= pivot]
        greater = [i for i in data[1:] if i > pivot]
        return sort(less) + [pivot] + sort(greater)

