def longestCommonPrefix(strings):
    prefix = ""
    min_length = min([len(s) for s in strings])
    for i in range(min_length):
        current_char = strings[0][i]
        for s in strings:
            if s[i] != current_char:
                return prefix
        prefix += current_char
    return prefix

