            queue.append(Puzzle(new_board, puzzle.steps + 1, cost_function(Puzzle(new_board, puzzle.steps + 1, 0))))

        # Reorder queue to the lowest cost board
        queue = deque(sorted(queue, key=lambda x: x.cost))

