    if len(numbers) <= 1:
        return numbers 
    else:
        pivot = numbers[0]
        less = [i for i in numbers[1:] if i <= pivot]
        greater = [i for i in numbers[1:] if i > pivot]
        return quick_sort(less) + [pivot] + quick_sort(greater)

numbers = [8,5,4,7,3,1]
sorted_numbers = quick_sort(numbers)
